const React = require("react");
const {
  BrowserRouter,
  Routes,
  Route,
  Navigate
} = require("react-router-dom");
const AppLayout = require("./components/AppLayout");
const Movies = require("./components/Movies");
const MovieDetails = require("./components/MovieDetails");
const Profile = require("./components/Profile");
const NotFound = require("./components/NotFound");
const VerifyUser = require("./components/VerifyUser");
const { Auth0Provider, useAuth0 } = require("@auth0/auth0-react");
const { AuthTokenProvider } = require("./AuthTokenContext");
const Home = require("./components/Home");
const ReactDOMClient = require("react-dom/client");


require("./style/normalize.css");
require("./style/index.css");


const container = document.getElementById("root");

const requestedScopes = ["profile", "email"];

function RequireAuth({ children }) {
  const { isAuthenticated, isLoading } = useAuth0();

  if (!isLoading && !isAuthenticated) {
    return <Navigate to="/" replace />;
  }


  return children;
}

const root = ReactDOMClient.createRoot(container);

root.render(
  <React.StrictMode>
    <Auth0Provider
      domain={process.env.REACT_APP_AUTH0_DOMAIN}
      clientId={process.env.REACT_APP_AUTH0_CLIENT_ID}
      authorizationParams={{
        redirect_uri: `${window.location.origin}/verify-user`,
        audience: process.env.REACT_APP_AUTH0_AUDIENCE,
        scope: requestedScopes.join(" "),
      }}
    >
      <AuthTokenProvider>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="verify-user" element={<VerifyUser />} />
            <Route path="movies" element={<Movies />} />
            <Route path="movies/:movieId" element={<MovieDetails />} />

            <Route
              path="app"
              element={
                <RequireAuth>
                  <AppLayout>
                    <Route index element={<Profile />} />
                  </AppLayout>
                </RequireAuth>
              }
            />
            <Route path="*" element={<NotFound />} />
          </Routes>
        </BrowserRouter>
      </AuthTokenProvider>
    </Auth0Provider>
  </React.StrictMode>
);
