import React, { useState, useEffect } from "react";

const MovieSearch = ({ searchState }) => {
  const { searchResults, setSearchResults } = searchState;
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMovieData = async () => {
      try {
        const response = await fetch(
          "https://movie-database-alternative.p.rapidapi.com/?s=Avengers Endgame&r=json&page=1",
          {
            method: "GET",
            headers: {
              "X-RapidAPI-Key": "0fce791ab6msh6f1b63a932f1ed3p1ef0b5jsn414243b1e98c",
              "X-RapidAPI-Host": "movie-database-alternative.p.rapidapi.com",
            },
          }
        );

        if (!response.ok) {
          throw new Error("Failed to fetch movie data");
        }

        const data = await response.json();
        setSearchResults(data.Search || []);
      } catch (error) {
        console.error("Error fetching movie data:", error);
        setError("Failed to fetch movie data");
      }
    };

    fetchMovieData();
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  return (
    <div>
      <h2>Movie Search Results</h2>
      {error && <p>{error}</p>}
      <ul>
        {searchResults.length > 0 && (
            <li key={searchResults[0].imdbID}>
                <p>Title: {searchResults[0].Title}</p>
                <p>Year: {searchResults[0].Year}</p>
                <p>Type: {searchResults[0].Type}</p>
                <img src={searchResults[0].Poster} alt={`Poster for ${searchResults[0].Title}`} />
            </li>
        )}
      </ul>
    </div>
  );
};

export default MovieSearch;
