import React, { useState, useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useAuthToken } from "../AuthTokenContext";


export default function Profile() {
  const { user } = useAuth0();
  const [movieName, setMovieName] = useState("");
  const [movieReview, setMovieReview] = useState("");
  const [userMovies, setUserMovies] = useState([]);
  const { accessToken } = useAuthToken();
  const [editingReviewId, setEditingReviewId] = useState(null);
  const [editedReviewContent, setEditedReviewContent] = useState("");

  console.log(accessToken)
  const fetchUserMovies = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/my-movies`, {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${accessToken}`,
        },
      });
      const movies = await response.json();
      setUserMovies(movies);
    } catch (error) {
      console.error("Error fetching user's movies:", error);
    }
  };

  useEffect(() => {
    console.log("userMovies before rendering:", userMovies);
    // Add any other logic you need before rendering

    // Example: Fetch user movies
    fetchUserMovies(user.sub);  // Assuming user.sub is the user's unique identifier
  }, [user, userMovies]);  // Add any dependencies you want to watch for changes




const handleEditReview = (reviewId, currentContent) => {
  setEditingReviewId(reviewId);
  setEditedReviewContent(currentContent);
};

const handleCancelEdit = () => {
  setEditingReviewId(null);
  setEditedReviewContent("");
};

const handleSaveEdit = async () => {
  try {
    const response = await fetch(`${process.env.REACT_APP_API_URL}/reviews/${editingReviewId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${accessToken}`,
      },
      body: JSON.stringify({
        content: editedReviewContent,
      }),
    });

    if (response.ok) {
      fetchUserMovies();
      setEditingReviewId(null);
      setEditedReviewContent("");
    } else {
      console.error("Failed to save edited review");
    }
  } catch (error) {
    console.error("Error saving edited review:", error);
  }
};



  const handleDeleteReview = async (reviewId) => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/reviews/${reviewId}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${accessToken}`,
        },
      });

      if (response.ok) {
        fetchUserMovies();
      } else {
        console.error("Failed to delete review");
      }
    } catch (error) {
      console.error("Error deleting review:", error);
    }
  };

  const handleCreateReview = async () => {
    try {
      const response = await fetch(`${process.env.REACT_APP_API_URL}/reviews`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${accessToken}`,
        },
        body: JSON.stringify({
          movieTitle: movieName,
          content: movieReview,
        }),
      });

      if (response.ok) {
        const newReview = await response.json();
        const reviewId = newReview.id;

        fetchUserMovies();

        setMovieName("");
        setMovieReview("");
      } else {
        console.error("Failed to create review");
      }
    } catch (error) {
      console.error("Error creating review:", error);
    }
  };

  return (
    <div>
      <div>
        <p>Name: {user.nickname}</p>
        <p>Email: {user.email}</p>
        <p>Auth0Id: {user.sub}</p>
        <p>Email Verified: {user.email_verified?.toString()}</p>
      </div>

      <div>
        <h2>Your Movie Reviews</h2>

        {userMovies.length > 0 ? (
        <ul>
          {userMovies.map((movie) => (
            <div key={movie.id}>
              <h3>{movie.title}</h3>
              {movie.reviews.map((review) => (
                <div key={review.id}>
                  {editingReviewId === review.id ? (
                    <div>
                      <textarea
                        value={editedReviewContent}
                        onChange={(e) => setEditedReviewContent(e.target.value)}
                      />
                      <button onClick={handleSaveEdit}>Save</button>
                      <button onClick={handleCancelEdit}>Cancel</button>
                    </div>
                  ) : (
                    <div>
                      <p>{review.content}</p>
                      <button onClick={() => handleEditReview(review.id, review.content)}>
                        Edit Review
                      </button>
                      <button onClick={() => handleDeleteReview(review.id)}>Delete Review</button>
                    </div>
                  )}
                </div>
              ))}
            </div>
          ))}
        </ul>
      ) : (
        <p>No movies available.</p>
      )}
      </div>
    </div>
  );
}
